import { useForm } from "react-hook-form"; 

const Formulario = () => {
    const { register, formState: { errors }, handleSubmit } = useForm();

const onSubmit = (data) =>{
    console.log(data);
}

    return <div>
        <h2>Formulario</h2>

        <form onSubmit={handleSubmit(onSubmit)}>
            <input type="text" {...register('Name',{
            required: true
            })} id="name" class="Name" placeholder="Nombre" />{errors.Name?.type === 'required' && <p>Escriba un nombre.</p>}
            
            <input type="text" {...register('Lnd', {
                required: true
            })} id="Lnd" class="Lnd" placeholder="Apellido Paterno" /> {errors.Lnd?.type === 'required' && <p>Escriba el apellido paterno.</p>}

            <input type="text" {...register('Lnm')} id="Lnm" class="Lnm" placeholder="Apellido Materno" />

            <label htmlFor="gender">Genero</label>
            
            <label htmlFor="Masculino">Masculino</label>
            <input type="radio" {...register('gender')} id="Male" class="Male" value="Hombre" />
            
            <label htmlFor="Femenino">Femenino</label>
            <input type="radio" {...register('gender')} id="Female" class="Female" value="Mujer" />
            
            <input type="n" {...register('Phone',{
            minLength: 10,
            maxLength:10
            })} id="Phone" class="Phone" placeholder="Teléfono" /> 
            {errors.Phone?.minLength === 'required' && <p>Escriba un teléfono con 10 digitos.</p>}
            {errors.Phone?.maxLength === 'required' && <p>Escriba un teléfono válido.</p>}
            
            <input type="email" {...register('Emal',{
                pattern: /^[^\s@]+@[^\s@]+\.[^\s@]+$/i
            })} calss="Email" placeholder="Correo Eelectrónico" /> {errors.Email?.type === 'pattern' && <p>No es un correo válido.</p>}

            <input type="submit" value="Enviar" />
        </form>
    </div>
}

export default Formulario;
