import { useForm } from "react-hook-form";

const Formulario = () => {
    const { register, formState: { errors }, handleSubmit } = useForm();

    const onSubmit = (data) => {
        console.log(data);
    }

    return <div>

        <form onSubmit={handleSubmit(onSubmit)}>
        <h1>FORMULARIO </h1>

            <input type="text" 
            {...register('Name', {
                required: true
            })} 
            id="name" class="Name" placeholder="Nombre" />{errors.Name?.type === 'required' && <p>Escriba un nombre.</p>}

            <input type="text" 
            {...register('Lnd', {
                required: true
            })} 
            id="Lnd" class="Lnd" placeholder="Apellido Paterno" /> {errors.Lnd?.type === 'required' && <p>Escriba el apellido paterno.</p>}

            
            <input type="email" 
            {...register('Email', {
                pattern: /^[^\s@]+@[^\s@]+\.[^\s@]+$/i
            })} 
           id="Email" class="Email" placeholder="Correo Eelectrónico" />{errors.Email?.type === 'pattern' && <p>No es un correo válido.</p>}

            <input type="submit" class="btnSubmit" value="ENVIAR" />
        </form>
    </div>
}

export default Formulario;
