import { useForm } from "react-hook-form"; 

const Formulario = () => {
    const { register, formState: { errors }, handleSubmit } = useForm();

const onSubmit = (data) =>{
    console.log(data);
}

    return <div>
        <h2>Formulario</h2>

        <form onSubmit={handleSubmit(onSubmit)}>
            <input type="text" {...register('Name')} name="Name" id="name" class="Name" placeholder="Nombre" /> {errors.Name?.type === 'required' && <p>Escriba un nombre.</p>}
            <input type="text" {...register('Lnd')} name="Lnd" id="Lnd" class="Lnd" placeholder="Apellido Paterno" /> {errors.Lnd?.type === 'required' && <p>Escriba el apellido paterno.</p>}
            <input type="text" {...register('Lnm')} name="Lnm" id="Lnm" class="Lnm" placeholder="Apellido Materno" />
            <label htmlFor="gender">Genero</label>
            <br />
            <label htmlFor="Masculino">Masculino</label>
            <input type="radio" {...register('Male')} name="gender" id="Male" class="Male" value="Hombre" />
            <input type="radio" {...register('Female')} name="gender" id="Female" class="Female" value="Mujer" />
            <input type="tel" {...register('Phone',{
            require: true,
            minLength: 10,
            maxLength:10
            })} name="Phone" id="Phone" class="Phone" placeholder="Teléfono" /> {errors.Phone?.Phone === 'required' && <p>Escriba un teléfono válido.</p>}
            <input type="email" {...register('Emal',{
                pattern: /^[^\s@]+@[^\s@]+\.[^\s@]+$/i
            })} name="Email" id="Email" calss="Email" placeholder="Correo Eelectrónico" /> {errors.Email?.type === 'pattern' && <p>No es un correo válido.</p>}

            <input type="submit" value="Enviar" />
        </form>
    </div>
}

export default Formulario;
